@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: #0a0a0a;
  --foreground: #ededed;
}

body {
  background-color: var(--background);
  color: var(--foreground);
  font-family: 'Geist', 'Inter', system-ui, -apple-system, sans-serif;
  overflow-x: hidden;
}

::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #111;
}

::-webkit-scrollbar-thumb {
  background: #333;
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #444;
}

.glass {
  background: rgba(255, 255, 255, 0.03);
  backdrop-filter: blur(12px);
  border: 1px solid rgba(255, 255, 255, 0.05);
}

.gradient-text {
  background: linear-gradient(to right, #fff, #888);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

import React from 'react';
import { Home, Library, PlayCircle, Film, Search, Settings, Plus } from 'lucide-react';
import { clsx, type ClassValue } from 'clsx';
import { twMerge } from 'tailwind-merge';

function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

const NavItem = ({ icon: Icon, label, active = false }: { icon: any, label: string, active?: boolean }) => (
  <button className={cn(
    "flex items-center gap-4 w-full px-4 py-3 rounded-xl transition-all duration-200 group",
    active ? "bg-accent/10 text-accent" : "text-gray-400 hover:bg-white/5 hover:text-white"
  )}>
    <Icon className={cn("w-5 h-5", active ? "text-accent" : "group-hover:scale-110 transition-transform")} />
    <span className="font-medium">{label}</span>
  </button>
);

export const Sidebar = () => {
  return (
    <aside className="w-64 h-full border-r border-white/5 bg-black flex flex-col p-6 hidden md:flex">
      <div className="flex items-center gap-3 px-4 mb-10">
        <div className="w-8 h-8 bg-accent rounded-lg flex items-center justify-center">
            <PlayCircle className="text-white w-5 h-5" />
        </div>
        <h1 className="text-xl font-bold tracking-tight">StreamX</h1>
      </div>

      <nav className="space-y-1 mb-10">
        <NavItem icon={Home} label="Home" active />
        <NavItem icon={Search} label="Search" />
        <NavItem icon={Library} label="Your Library" />
      </nav>

      <div className="mb-4 px-4 text-xs font-semibold text-gray-500 uppercase tracking-wider">
        Collections
      </div>
      <nav className="space-y-1 flex-1">
        <NavItem icon={PlayCircle} label="Music" />
        <NavItem icon={Film} label="Videos" />
      </nav>

      <div className="mt-auto pt-6 border-t border-white/5">
        <button className="flex items-center gap-4 w-full px-4 py-3 rounded-xl bg-white/5 text-white hover:bg-white/10 transition-all">
          <Plus className="w-5 h-5" />
          <span className="font-medium">Add Media</span>
        </button>
      </div>
    </aside>
  );
};

import React from 'react';
import { Play, MoreVertical } from 'lucide-react';
import { motion } from 'framer-motion';

interface MediaItem {
  id: string;
  title: string;
  artist: string;
  type: 'audio' | 'video';
  thumbnail: string;
  duration: string;
  url: string;
}

interface MediaGridProps {
  items: MediaItem[];
  onSelect: (item: MediaItem) => void;
}

export const MediaGrid = ({ items, onSelect }: MediaGridProps) => {
  return (
    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-6">
      {items.map((item, index) => (
        <motion.div
          key={item.id}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: index * 0.05 }}
          className="group cursor-pointer"
          onClick={() => onSelect(item)}
        >
          <div className="relative aspect-square overflow-hidden rounded-2xl mb-4">
            <img 
              src={item.thumbnail} 
              alt={item.title}
              className="object-cover w-full h-full transition-transform duration-500 group-hover:scale-110"
            />
            <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
              <div className="w-14 h-14 bg-accent rounded-full flex items-center justify-center shadow-2xl transform translate-y-4 group-hover:translate-y-0 transition-transform">
                <Play className="fill-white text-white w-6 h-6 ml-1" />
              </div>
            </div>
            <div className="absolute top-3 right-3 opacity-0 group-hover:opacity-100 transition-opacity">
              <button className="p-2 bg-black/50 backdrop-blur-md rounded-full text-white">
                <MoreVertical className="w-4 h-4" />
              </button>
            </div>
            <div className="absolute bottom-3 left-3 px-2 py-1 bg-black/50 backdrop-blur-md rounded text-[10px] font-bold text-white uppercase tracking-wider">
              {item.type}
            </div>
          </div>
          <h3 className="font-semibold text-white truncate leading-tight">{item.title}</h3>
          <p className="text-sm text-gray-500 truncate mt-1">{item.artist}</p>
        </motion.div>
      ))}
    </div>
  );
};

import React, { useRef, useEffect, useState } from 'react';
import { Play, Pause, SkipBack, SkipForward, Volume2, Maximize2, X, Repeat, Shuffle } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

interface MediaItem {
  id: string;
  title: string;
  artist: string;
  type: 'audio' | 'video';
  thumbnail: string;
  url: string;
}

interface PlayerProps {
  item: MediaItem | null;
  onClose: () => void;
}

export const Player = ({ item, onClose }: PlayerProps) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const audioRef = useRef<HTMLAudioElement>(null);
  const [isPlaying, setIsPlaying] = useState(true);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    setIsPlaying(true);
    setProgress(0);
  }, [item]);

  const togglePlay = () => {
    const media = item?.type === 'video' ? videoRef.current : audioRef.current;
    if (media) {
      if (isPlaying) {
        media.pause();
      } else {
        media.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleTimeUpdate = (e: any) => {
    const { currentTime, duration } = e.target;
    setProgress((currentTime / duration) * 100);
  };

  if (!item) return null;

  return (
    <motion.div 
      initial={{ y: 100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      className="fixed bottom-0 left-0 right-0 z-50 px-4 pb-4 md:px-10 md:pb-10 pointer-events-none"
    >
      <div className="max-w-7xl mx-auto glass rounded-3xl p-4 flex flex-col md:flex-row items-center gap-6 pointer-events-auto shadow-2xl">

        {/* Thumbnail/Video Area */}
        <div className="relative w-20 h-20 md:w-24 md:h-24 rounded-2xl overflow-hidden flex-shrink-0 bg-black">
          {item.type === 'video' ? (
            <video 
              ref={videoRef}
              src={item.url}
              autoPlay
              onTimeUpdate={handleTimeUpdate}
              className="w-full h-full object-cover"
              onClick={togglePlay}
            />
          ) : (
            <>
              <img src={item.thumbnail} className="w-full h-full object-cover opacity-50" />
              <audio ref={audioRef} src={item.url} autoPlay onTimeUpdate={handleTimeUpdate} />
              <div className="absolute inset-0 flex items-center justify-center">
                 <div className="w-8 h-8 rounded-full border-2 border-accent border-t-transparent animate-spin" />
              </div>
            </>
          )}
        </div>

        {/* Info Area */}
        <div className="flex-1 min-w-0 flex flex-col items-center md:items-start">
          <h4 className="font-bold text-lg truncate w-full text-center md:text-left">{item.title}</h4>
          <p className="text-sm text-gray-400 truncate w-full text-center md:text-left">{item.artist}</p>
        </div>

        {/* Controls */}
        <div className="flex flex-col items-center gap-2 w-full md:w-1/3">
          <div className="flex items-center gap-6">
            <button className="text-gray-400 hover:text-white transition-colors"><Shuffle className="w-4 h-4" /></button>
            <button className="text-gray-400 hover:text-white transition-colors"><SkipBack className="w-6 h-6 fill-current" /></button>
            <button 
              onClick={togglePlay}
              className="w-12 h-12 bg-white text-black rounded-full flex items-center justify-center hover:scale-105 transition-transform"
            >
              {isPlaying ? <Pause className="w-6 h-6 fill-current" /> : <Play className="w-6 h-6 fill-current ml-1" />}
            </button>
            <button className="text-gray-400 hover:text-white transition-colors"><SkipForward className="w-6 h-6 fill-current" /></button>
            <button className="text-gray-400 hover:text-white transition-colors"><Repeat className="w-4 h-4" /></button>
          </div>

          <div className="w-full h-1 bg-white/10 rounded-full overflow-hidden">
            <motion.div 
               className="h-full bg-accent"
               animate={{ width: `${progress}%` }}
               transition={{ type: 'spring', bounce: 0, duration: 0.1 }}
            />
          </div>
        </div>

        {/* Action area */}
        <div className="hidden md:flex items-center gap-4 w-48 justify-end">
          <Volume2 className="w-5 h-5 text-gray-400" />
          <div className="w-20 h-1 bg-white/10 rounded-full">
            <div className="w-2/3 h-full bg-white/40 rounded-full" />
          </div>
          <button onClick={onClose} className="p-2 hover:bg-white/10 rounded-full transition-colors">
            <X className="w-5 h-5 text-gray-400 hover:text-white" />
          </button>
        </div>
      </div>
    </motion.div>
  );
};

import React, { useState, useMemo } from 'react';
import { Sidebar } from './components/Sidebar';
import { MediaGrid } from './components/MediaGrid';
import { Player } from './components/Player';
import { Search, Bell, User, Filter } from 'lucide-react';
import mediaData from './data.json';

function App() {
  const [selectedMedia, setSelectedMedia] = useState<any>(null);
  const [filter, setFilter] = useState<'all' | 'audio' | 'video'>('all');
  const [searchQuery, setSearchQuery] = useState('');

  const filteredData = useMemo(() => {
    return mediaData.filter((item) => {
      const matchesFilter = filter === 'all' || item.type === filter;
      const matchesSearch = item.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                           item.artist.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesFilter && matchesSearch;
    });
  }, [filter, searchQuery]);

  return (
    <div className="flex h-screen bg-background text-foreground overflow-hidden font-sans">
      <Sidebar />

      <main className="flex-1 flex flex-col min-w-0 relative">
        {/* Header */}
        <header className="h-20 flex items-center justify-between px-8 border-b border-white/5 bg-black/50 backdrop-blur-md z-10">
          <div className="flex items-center gap-4 bg-white/5 px-4 py-2 rounded-2xl w-full max-w-md border border-white/5 focus-within:border-accent/50 transition-colors">
            <Search className="w-5 h-5 text-gray-500" />
            <input 
              type="text" 
              placeholder="Search music, videos, artists..." 
              className="bg-transparent border-none outline-none w-full text-sm py-1"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>

          <div className="flex items-center gap-6">
            <div className="hidden sm:flex items-center gap-2">
              <button 
                onClick={() => setFilter('all')}
                className={`px-4 py-2 rounded-xl text-sm font-medium transition-colors ${filter === 'all' ? 'bg-white text-black' : 'hover:bg-white/5'}`}
              >
                All
              </button>
              <button 
                onClick={() => setFilter('audio')}
                className={`px-4 py-2 rounded-xl text-sm font-medium transition-colors ${filter === 'audio' ? 'bg-white text-black' : 'hover:bg-white/5'}`}
              >
                Music
              </button>
              <button 
                onClick={() => setFilter('video')}
                className={`px-4 py-2 rounded-xl text-sm font-medium transition-colors ${filter === 'video' ? 'bg-white text-black' : 'hover:bg-white/5'}`}
              >
                Videos
              </button>
            </div>
            <div className="w-[1px] h-6 bg-white/10 hidden sm:block" />
            <button className="relative p-2 text-gray-400 hover:text-white transition-colors">
              <Bell className="w-6 h-6" />
              <span className="absolute top-2 right-2 w-2 h-2 bg-accent rounded-full border-2 border-background" />
            </button>
            <div className="w-10 h-10 rounded-full bg-gradient-to-tr from-accent to-orange-400 p-[2px] cursor-pointer active:scale-95 transition-transform">
               <div className="w-full h-full rounded-full bg-black flex items-center justify-center">
                  <User className="w-5 h-5" />
               </div>
            </div>
          </div>
        </header>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-8 custom-scrollbar">
          <section className="mb-12">
            <div className="flex items-end justify-between mb-8">
              <div>
                <h2 className="text-4xl font-black tracking-tight mb-2 gradient-text uppercase italic">Library</h2>
                <p className="text-gray-500 font-medium">Your personal collection of media.</p>
              </div>
              <button className="flex items-center gap-2 text-sm font-bold text-accent hover:text-accent-hover transition-colors">
                <Filter className="w-4 h-4" />
                SORT BY: RECENTLY ADDED
              </button>
            </div>

            <MediaGrid 
              items={filteredData} 
              onSelect={(item) => setSelectedMedia(item)} 
            />
          </section>

          {/* Empty State */}
          {filteredData.length === 0 && (
            <div className="flex flex-col items-center justify-center py-20 text-center">
              <div className="w-20 h-20 bg-white/5 rounded-full flex items-center justify-center mb-6">
                <Search className="w-10 h-10 text-gray-700" />
              </div>
              <h3 className="text-xl font-bold mb-2">No results found</h3>
              <p className="text-gray-500">Try searching for something else or check your filters.</p>
            </div>
          )}
        </div>

        {/* Global Player */}
        <Player 
          item={selectedMedia} 
          onClose={() => setSelectedMedia(null)} 
        />
      </main>
    </div>
  );
}

export default App;

