import React from 'react';
import { Play, MoreVertical } from 'lucide-react';
import { motion } from 'framer-motion';

interface MediaItem {
  id: string;
  title: string;
  artist: string;
  type: 'audio' | 'video';
  thumbnail: string;
  duration: string;
  url: string;
}

interface MediaGridProps {
  items: MediaItem[];
  onSelect: (item: MediaItem) => void;
}

export const MediaGrid = ({ items, onSelect }: MediaGridProps) => {
  return (
    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-6">
      {items.map((item, index) => (
        <motion.div
          key={item.id}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: index * 0.05 }}
          className="group cursor-pointer"
          onClick={() => onSelect(item)}
        >
          <div className="relative aspect-square overflow-hidden rounded-2xl mb-4">
            <img 
              src={item.thumbnail} 
              alt={item.title}
              className="object-cover w-full h-full transition-transform duration-500 group-hover:scale-110"
            />
            <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
              <div className="w-14 h-14 bg-accent rounded-full flex items-center justify-center shadow-2xl transform translate-y-4 group-hover:translate-y-0 transition-transform">
                <Play className="fill-white text-white w-6 h-6 ml-1" />
              </div>
            </div>
            <div className="absolute top-3 right-3 opacity-0 group-hover:opacity-100 transition-opacity">
              <button className="p-2 bg-black/50 backdrop-blur-md rounded-full text-white">
                <MoreVertical className="w-4 h-4" />
              </button>
            </div>
            <div className="absolute bottom-3 left-3 px-2 py-1 bg-black/50 backdrop-blur-md rounded text-[10px] font-bold text-white uppercase tracking-wider">
              {item.type}
            </div>
          </div>
          <h3 className="font-semibold text-white truncate leading-tight">{item.title}</h3>
          <p className="text-sm text-gray-500 truncate mt-1">{item.artist}</p>
        </motion.div>
      ))}
    </div>
  );
};

