import { useState, useMemo } from 'react';
import { Sidebar } from './components/Sidebar';
import { MediaGrid } from './components/MediaGrid';
import { Player } from './components/Player';
import { Search, Bell, User, Filter } from 'lucide-react';
import mediaData from './data.json';

function App() {
  const [selectedMedia, setSelectedMedia] = useState<any>(null);
  const [filter, setFilter] = useState<'all' | 'audio' | 'video'>('all');
  const [searchQuery, setSearchQuery] = useState('');

  const filteredData = useMemo(() => {
    return (mediaData as any[]).filter((item) => {
      const matchesFilter = filter === 'all' || item.type === filter;
      const matchesSearch = item.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                           item.artist.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesFilter && matchesSearch;
    });
  }, [filter, searchQuery]);

  return (
    <div className="flex h-screen bg-background text-foreground overflow-hidden font-sans mesh-gradient">
      <Sidebar />

      <main className="flex-1 flex flex-col min-w-0 relative">
        {/* Header */}
        <header className="h-20 flex items-center justify-between px-8 bg-black/20 backdrop-blur-xl z-10">
          <div className="flex items-center gap-4 bg-white/5 px-4 py-2.5 rounded-2xl w-full max-w-md border border-white/5 focus-within:border-accent/50 focus-within:bg-white/10 transition-all duration-300">
            <Search className="w-5 h-5 text-gray-400" />
            <input 
              type="text" 
              placeholder="Search music, videos, artists..." 
              className="bg-transparent border-none outline-none w-full text-sm py-1 placeholder:text-gray-500"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>

          <div className="flex items-center gap-6">
            <div className="hidden sm:flex items-center p-1 bg-white/5 rounded-2xl border border-white/5">
              <button 
                onClick={() => setFilter('all')}
                className={`px-5 py-1.5 rounded-xl text-sm font-semibold transition-all duration-300 ${filter === 'all' ? 'bg-white text-black shadow-lg shadow-white/10' : 'text-gray-400 hover:text-white'}`}
              >
                All
              </button>
              <button 
                onClick={() => setFilter('audio')}
                className={`px-5 py-1.5 rounded-xl text-sm font-semibold transition-all duration-300 ${filter === 'audio' ? 'bg-white text-black shadow-lg shadow-white/10' : 'text-gray-400 hover:text-white'}`}
              >
                Music
              </button>
              <button 
                onClick={() => setFilter('video')}
                className={`px-5 py-1.5 rounded-xl text-sm font-semibold transition-all duration-300 ${filter === 'video' ? 'bg-white text-black shadow-lg shadow-white/10' : 'text-gray-400 hover:text-white'}`}
              >
                Videos
              </button>
            </div>
            <div className="w-[1px] h-6 bg-white/10 hidden sm:block" />
            <button className="relative p-2.5 rounded-xl bg-white/5 hover:bg-white/10 transition-colors border border-white/5">
              <Bell className="w-5 h-5 text-gray-300" />
              <span className="absolute top-2.5 right-2.5 w-2 h-2 bg-blue-500 rounded-full border-2 border-background" />
            </button>
            <div className="w-10 h-10 rounded-full bg-gradient-to-tr from-blue-500 to-purple-500 p-[2px] cursor-pointer hover:scale-105 active:scale-95 transition-all shadow-lg shadow-blue-500/20">
               <div className="w-full h-full rounded-full bg-black flex items-center justify-center">
                  <User className="w-5 h-5" />
               </div>
            </div>
          </div>
        </header>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-8 custom-scrollbar">
          <section className="mb-12">
            <div className="flex items-end justify-between mb-8">
              <div>
                <h2 className="text-5xl font-extrabold tracking-tight mb-3 gradient-text">Library</h2>
                <div className="h-1.5 w-12 bg-blue-500 rounded-full mb-2"></div>
                <p className="text-gray-400 font-medium">Your personal collection, curated by you.</p>
              </div>
              <button className="flex items-center gap-2 px-4 py-2 rounded-xl text-xs font-bold tracking-widest text-gray-400 hover:text-white hover:bg-white/5 transition-all border border-white/5 uppercase">
                <Filter className="w-3.5 h-3.5" />
                Sort: Recent
              </button>
            </div>

            <MediaGrid 
              items={filteredData} 
              onSelect={(item) => setSelectedMedia(item)} 
            />
          </section>

          {/* Empty State */}
          {filteredData.length === 0 && (
            <div className="flex flex-col items-center justify-center py-20 text-center">
              <div className="w-20 h-20 bg-white/5 rounded-full flex items-center justify-center mb-6">
                <Search className="w-10 h-10 text-gray-700" />
              </div>
              <h3 className="text-xl font-bold mb-2">No results found</h3>
              <p className="text-gray-500">Try searching for something else or check your filters.</p>
            </div>
          )}
        </div>

        {/* Global Player */}
        <Player 
          item={selectedMedia} 
          onClose={() => setSelectedMedia(null)} 
        />
      </main>
    </div>
  );
}

export default App;

import React from 'react';
import { Play, MoreVertical } from 'lucide-react';
import { motion } from 'framer-motion';

interface MediaItem {
  id: string;
  title: string;
  artist: string;
  type: 'audio' | 'video';
  thumbnail: string;
  duration: string;
  url: string;
}

interface MediaGridProps {
  items: MediaItem[];
  onSelect: (item: MediaItem) => void;
}

export const MediaGrid = ({ items, onSelect }: MediaGridProps) => {
  return (
    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-6">
      {items.map((item, index) => (
        <motion.div
          key={item.id}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: index * 0.05 }}
          className="group cursor-pointer p-3 rounded-[24px] bg-white/[0.03] border border-white/[0.05] hover:bg-white/[0.08] hover:border-white/[0.1] hover:shadow-2xl transition-all duration-300"
          onClick={() => onSelect(item)}
        >
          <div className="relative aspect-square overflow-hidden rounded-[18px] mb-4 shadow-xl">
            <img 
              src={item.thumbnail} 
              alt={item.title}
              className="object-cover w-full h-full transition-transform duration-700 group-hover:scale-105"
            />
            <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center backdrop-blur-[2px]">
              <div className="w-14 h-14 bg-blue-500 rounded-full flex items-center justify-center shadow-[0_0_30px_rgba(59,130,246,0.5)] transform scale-90 group-hover:scale-100 transition-transform duration-300">
                <Play className="fill-white text-white w-6 h-6 ml-1" />
              </div>
            </div>
            <div className="absolute top-3 right-3 opacity-0 group-hover:opacity-100 transition-opacity">
              <button className="p-2.5 bg-black/50 backdrop-blur-md rounded-full text-white border border-white/10 hover:bg-black/70 transition-colors">
                <MoreVertical className="w-4 h-4" />
              </button>
