            <p className="text-sm font-medium text-gray-400 truncate flex items-center gap-2">
              <span className="w-1.5 h-1.5 rounded-full bg-blue-500/50"></span>
              {item.artist}
            </p>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

import React, { useRef, useEffect, useState } from 'react';
import { Play, Pause, SkipBack, SkipForward, Volume2, Maximize2, X, Repeat, Shuffle } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

interface MediaItem {
  id: string;
  title: string;
  artist: string;
  type: 'audio' | 'video';
  thumbnail: string;
  url: string;
}

interface PlayerProps {
  item: MediaItem | null;
  onClose: () => void;
}

export const Player = ({ item, onClose }: PlayerProps) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const audioRef = useRef<HTMLAudioElement>(null);
  const [isPlaying, setIsPlaying] = useState(true);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    setIsPlaying(true);
    setProgress(0);
  }, [item]);

  const togglePlay = () => {
    const media = item?.type === 'video' ? videoRef.current : audioRef.current;
    if (media) {
      if (isPlaying) {
        media.pause();
      } else {
        media.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleTimeUpdate = (e: any) => {
    const { currentTime, duration } = e.target;
    setProgress((currentTime / duration) * 100);
  };

  if (!item) return null;

  return (
    <motion.div 
      initial={{ y: 100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      className="fixed bottom-0 left-0 right-0 z-50 px-4 pb-4 md:px-10 md:pb-10 pointer-events-none"
    >
      <div className="max-w-7xl mx-auto glass rounded-3xl p-4 flex flex-col md:flex-row items-center gap-6 pointer-events-auto shadow-2xl">

        {/* Thumbnail/Video Area */}
        <div className="relative w-20 h-20 md:w-24 md:h-24 rounded-2xl overflow-hidden flex-shrink-0 bg-black">
          {item.type === 'video' ? (
            <video 
              ref={videoRef}
              src={item.url}
              autoPlay
              onTimeUpdate={handleTimeUpdate}
              className="w-full h-full object-cover"
              onClick={togglePlay}
            />
          ) : (
            <>
              <img src={item.thumbnail} className="w-full h-full object-cover opacity-50" />
              <audio ref={audioRef} src={item.url} autoPlay onTimeUpdate={handleTimeUpdate} />
              <div className="absolute inset-0 flex items-center justify-center">
                 <div className="w-8 h-8 rounded-full border-2 border-accent border-t-transparent animate-spin" />
              </div>
            </>
          )}
        </div>

        {/* Info Area */}
        <div className="flex-1 min-w-0 flex flex-col items-center md:items-start">
          <h4 className="font-bold text-lg truncate w-full text-center md:text-left">{item.title}</h4>
          <p className="text-sm text-gray-400 truncate w-full text-center md:text-left">{item.artist}</p>
        </div>

        {/* Controls */}
        <div className="flex flex-col items-center gap-2 w-full md:w-1/3">
          <div className="flex items-center gap-6">
            <button className="text-gray-400 hover:text-white transition-colors"><Shuffle className="w-4 h-4" /></button>
            <button className="text-gray-400 hover:text-white transition-colors"><SkipBack className="w-6 h-6 fill-current" /></button>
            <button 
              onClick={togglePlay}
              className="w-12 h-12 bg-white text-black rounded-full flex items-center justify-center hover:scale-105 transition-transform"
            >
              {isPlaying ? <Pause className="w-6 h-6 fill-current" /> : <Play className="w-6 h-6 fill-current ml-1" />}
            </button>
            <button className="text-gray-400 hover:text-white transition-colors"><SkipForward className="w-6 h-6 fill-current" /></button>
            <button className="text-gray-400 hover:text-white transition-colors"><Repeat className="w-4 h-4" /></button>
          </div>

          <div className="w-full h-1 bg-white/10 rounded-full overflow-hidden">
            <motion.div 
               className="h-full bg-accent"
               animate={{ width: `${progress}%` }}
               transition={{ type: 'spring', bounce: 0, duration: 0.1 }}
            />
          </div>
        </div>

        {/* Action area */}
        <div className="hidden md:flex items-center gap-4 w-48 justify-end">
          <Volume2 className="w-5 h-5 text-gray-400" />
          <div className="w-20 h-1 bg-white/10 rounded-full">
            <div className="w-2/3 h-full bg-white/40 rounded-full" />
          </div>
          <button onClick={onClose} className="p-2 hover:bg-white/10 rounded-full transition-colors">
            <X className="w-5 h-5 text-gray-400 hover:text-white" />
          </button>
        </div>
      </div>
    </motion.div>
  );
};

