import React from 'react';
import { Home, Library, PlayCircle, Film, Search, Settings, Plus } from 'lucide-react';
import { clsx, type ClassValue } from 'clsx';
import { twMerge } from 'tailwind-merge';

function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

const NavItem = ({ icon: Icon, label, active = false }: { icon: any, label: string, active?: boolean }) => (
  <button className={cn(
    "flex items-center gap-4 w-full px-4 py-3 rounded-xl transition-all duration-200 group",
    active ? "bg-accent/10 text-accent" : "text-gray-400 hover:bg-white/5 hover:text-white"
  )}>
    <Icon className={cn("w-5 h-5", active ? "text-accent" : "group-hover:scale-110 transition-transform")} />
    <span className="font-medium">{label}</span>
  </button>
);

export const Sidebar = () => {
  return (
    <aside className="w-64 h-full border-r border-white/5 bg-black flex flex-col p-6 hidden md:flex">
      <div className="flex items-center gap-3 px-4 mb-10">
        <div className="w-8 h-8 bg-accent rounded-lg flex items-center justify-center">
            <PlayCircle className="text-white w-5 h-5" />
        </div>
        <h1 className="text-xl font-bold tracking-tight">StreamX</h1>
      </div>

      <nav className="space-y-1 mb-10">
        <NavItem icon={Home} label="Home" active />
        <NavItem icon={Search} label="Search" />
        <NavItem icon={Library} label="Your Library" />
      </nav>

      <div className="mb-4 px-4 text-xs font-semibold text-gray-500 uppercase tracking-wider">
        Collections
      </div>
      <nav className="space-y-1 flex-1">
        <NavItem icon={PlayCircle} label="Music" />
        <NavItem icon={Film} label="Videos" />
      </nav>

      <div className="mt-auto pt-6 border-t border-white/5">
        <button className="flex items-center gap-4 w-full px-4 py-3 rounded-xl bg-white/5 text-white hover:bg-white/10 transition-all">
          <Plus className="w-5 h-5" />
          <span className="font-medium">Add Media</span>
        </button>
      </div>
    </aside>
  );
};

