@import "tailwindcss";

@layer base {
  :root {
    --background: #050505;
    --foreground: #ffffff;
    --card: #121212;
    --accent: #3b82f6;
  }

  body {
    background-color: var(--background);
    color: var(--foreground);
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    -webkit-font-smoothing: antialiased;
  }
}

@layer components {
  .glass {
    @apply bg-white/5 backdrop-blur-[10px] border border-white/10 shadow-[0_8px_32px_0_rgba(0,0,0,0.4)];
  }

  .glass-panel {
    @apply bg-white/3 backdrop-blur-xl border border-white/5 shadow-[0_8px_32px_0_rgba(0,0,0,0.3)] rounded-3xl;
  }

  .card {
    @apply bg-white/2 backdrop-blur-[4px] border border-white/5 rounded-2xl transition-all duration-300;
  }

  .card-hover {
    @apply card hover:scale-[1.02] hover:bg-white/5 hover:shadow-[0_20px_40px_rgba(59,130,246,0.1)] hover:border-white/10;
  }

  .surface {
    @apply bg-gradient-to-br from-white/8 to-white/2 backdrop-blur-[8px] border border-white/10;
  }

  .neo-button {
    @apply relative overflow-hidden transition-all duration-300 active:scale-95 font-semibold;
  }

  .neo-button::after {
    @apply content-[''] absolute inset-0 bg-white/10 opacity-0 transition-opacity duration-300;
  }

  .neo-button:hover::after {
    @apply opacity-100;
  }

  .glow-accent {
    @apply shadow-[0_0_30px_rgba(59,130,246,0.4)];
  }

  .glow-lg {
    @apply shadow-[0_0_60px_rgba(59,130,246,0.3)];
  }

  .elevated {
    @apply shadow-[0_10px_40px_rgba(0,0,0,0.5)];
  }
}

.custom-scrollbar::-webkit-scrollbar {
  width: 6px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  @apply bg-transparent;
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  @apply bg-white/10 rounded-full hover:bg-white/20 transition-colors;
}

.gradient-text {
  @apply bg-clip-text text-transparent bg-gradient-to-br from-white via-white to-white/50;
}

.mesh-gradient {
  background-color: #050505;
  background-image: 
    radial-gradient(at 0% 0%, rgba(59, 130, 246, 0.15) 0px, transparent 50%),
    radial-gradient(at 100% 0%, rgba(147, 51, 234, 0.1) 0px, transparent 50%);
}

