/**
 * SelfDB SDK HTTP Client
 *
 * Base HTTP client for making API requests with authentication and error handling.
 */
/**
 * HTTP client configuration
 */
interface HttpClientConfig {
    baseUrl: string;
    apiKey: string;
    timeout?: number;
}
/**
 * Request options
 */
interface RequestOptions {
    method: 'GET' | 'POST' | 'PATCH' | 'DELETE' | 'PUT';
    path: string;
    body?: unknown;
    query?: Record<string, string | number | boolean | undefined>;
    headers?: Record<string, string>;
    rawBody?: boolean;
}
/**
 * HTTP Client for SelfDB API
 */
declare class HttpClient {
    private baseUrl;
    private apiKey;
    private timeout;
    private accessToken;
    private refreshToken;
    constructor(config: HttpClientConfig);
    /**
     * Set the access token for authenticated requests
     */
    setAccessToken(token: string | null): void;
    /**
     * Get the current access token
     */
    getAccessToken(): string | null;
    /**
     * Set the refresh token
     */
    setRefreshToken(token: string | null): void;
    /**
     * Get the current refresh token
     */
    getRefreshToken(): string | null;
    /**
     * Build query string from parameters
     */
    private buildQueryString;
    /**
     * Handle API errors based on status code
     */
    private handleError;
    /**
     * Make an HTTP request
     */
    request<T>(options: RequestOptions): Promise<T>;
    /**
     * GET request
     */
    get<T>(path: string, query?: Record<string, string | number | boolean | undefined>, headers?: Record<string, string>): Promise<T>;
    /**
     * POST request
     */
    post<T>(path: string, body?: unknown, query?: Record<string, string | number | boolean | undefined>, headers?: Record<string, string>): Promise<T>;
    /**
     * POST request with raw body (for file uploads)
     */
    postRaw<T>(path: string, body: BodyInit, query?: Record<string, string | number | boolean | undefined>, headers?: Record<string, string>): Promise<T>;
    /**
     * PATCH request
     */
    patch<T>(path: string, body?: unknown, query?: Record<string, string | number | boolean | undefined>, headers?: Record<string, string>): Promise<T>;
    /**
     * DELETE request
     */
    delete<T>(path: string, query?: Record<string, string | number | boolean | undefined>, headers?: Record<string, string>): Promise<T>;
}

/**
 * SelfDB SDK Models
 *
 * TypeScript interfaces and types for the SelfDB API.
 */
/**
 * User role enum (uppercase values)
 */
type UserRole = 'USER' | 'ADMIN';
/**
 * Request model for creating a user
 * NOTE: API uses camelCase for firstName/lastName
 */
interface UserCreate {
    email: string;
    password: string;
    firstName: string;
    lastName: string;
    role?: UserRole;
}
/**
 * Request model for updating a user (all fields optional)
 */
interface UserUpdate {
    firstName?: string;
    lastName?: string;
    password?: string;
    role?: UserRole;
}
/**
 * User response model
 */
interface UserRead {
    id: string;
    email: string;
    firstName: string;
    lastName: string;
    role: UserRole;
    createdAt: string;
    updatedAt: string;
}
/**
 * Login request
 */
interface LoginRequest {
    email: string;
    password: string;
}
/**
 * Token pair response (access + refresh tokens)
 */
interface TokenPair {
    access_token: string;
    refresh_token: string;
    token_type: string;
}
/**
 * Refresh token request
 */
interface RefreshRequest {
    refreshToken: string;
}
/**
 * Logout request
 */
interface LogoutRequest {
    refreshToken?: string;
}
/**
 * Logout response
 */
interface LogoutResponse {
    message: string;
}
/**
 * User delete response
 */
interface UserDeleteResponse {
    message: string;
    user_id: string;
}
/**
 * Supported column types
 */
type ColumnType = 'text' | 'varchar' | 'integer' | 'bigint' | 'boolean' | 'timestamp' | 'jsonb' | 'uuid';
/**
 * Column schema definition (flat object format, not JSON Schema)
 */
interface ColumnSchema {
    type: ColumnType;
    nullable?: boolean;
    default?: unknown;
}
/**
 * Table schema as flat object mapping column names to schemas
 */
interface TableSchema {
    [columnName: string]: ColumnSchema;
}
/**
 * Request model for creating a table
 */
interface TableCreate {
    name: string;
    table_schema: TableSchema;
    public: boolean;
}
/**
 * Request model for updating a table
 * NOTE: Uses realtime_enabled (not realtime)
 */
interface TableUpdate {
    name?: string;
    public?: boolean;
    realtime_enabled?: boolean;
}
/**
 * Column definition for adding a new column
 */
interface ColumnDefinition {
    name: string;
    type: ColumnType;
    nullable?: boolean;
    default_value?: unknown;
}
/**
 * Column update model
 */
interface ColumnUpdate {
    new_name?: string;
    type?: ColumnType;
    nullable?: boolean;
    default_value?: unknown;
}
