import { useState, useMemo } from 'react';
import { Sidebar } from './components/Sidebar';
import { MediaGrid } from './components/MediaGrid';
import { Player } from './components/Player';
import { Search, Bell, User, Filter } from 'lucide-react';
import mediaData from './data.json';

function App() {
  const [selectedMedia, setSelectedMedia] = useState<any>(null);
  const [filter, setFilter] = useState<'all' | 'audio' | 'video'>('all');
  const [searchQuery, setSearchQuery] = useState('');

  const filteredData = useMemo(() => {
    return (mediaData as any[]).filter((item) => {
      const matchesFilter = filter === 'all' || item.type === filter;
      const matchesSearch = item.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                           item.artist.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesFilter && matchesSearch;
    });
  }, [filter, searchQuery]);

  return (
    <div className="flex h-screen bg-background text-foreground overflow-hidden font-sans mesh-gradient">
      <Sidebar />

      <main className="flex-1 flex flex-col min-w-0 relative">
        {/* Header */}
        <header className="h-20 flex items-center justify-between px-8 bg-black/30 backdrop-blur-2xl z-10 border-b border-white/5 shadow-[0_4px_20px_rgba(0,0,0,0.3)]">
          <div className="flex items-center gap-4 glass px-5 py-3 rounded-2xl w-full max-w-md focus-within:shadow-[0_0_30px_rgba(59,130,246,0.2)] focus-within:border-blue-500/30 transition-all duration-300">
            <Search className="w-5 h-5 text-gray-500" />
            <input 
              type="text" 
              placeholder="Search music, videos, artists..." 
              className="bg-transparent border-none outline-none w-full text-sm py-1 placeholder:text-gray-600"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>

          <div className="flex items-center gap-6 ml-6">
            <div className="hidden sm:flex items-center p-1 glass rounded-2xl">
              <button 
                onClick={() => setFilter('all')}
                className={`px-5 py-1.5 rounded-xl text-sm font-semibold transition-all duration-300 ${filter === 'all' ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-lg shadow-blue-500/40' : 'text-gray-400 hover:text-white'}`}
              >
                All
              </button>
              <button 
                onClick={() => setFilter('audio')}
                className={`px-5 py-1.5 rounded-xl text-sm font-semibold transition-all duration-300 ${filter === 'audio' ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-lg shadow-blue-500/40' : 'text-gray-400 hover:text-white'}`}
              >
                Music
              </button>
              <button 
                onClick={() => setFilter('video')}
                className={`px-5 py-1.5 rounded-xl text-sm font-semibold transition-all duration-300 ${filter === 'video' ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-lg shadow-blue-500/40' : 'text-gray-400 hover:text-white'}`}
              >
                Videos
              </button>
            </div>
            <div className="w-[1px] h-6 bg-white/10 hidden sm:block" />
            <button className="relative p-2.5 rounded-xl bg-white/5 hover:bg-white/10 hover:border-white/10 transition-all border border-white/5 group">
              <Bell className="w-5 h-5 text-gray-400 group-hover:text-white transition-colors" />
              <span className="absolute top-2 right-2 w-2.5 h-2.5 bg-red-500 rounded-full border-2 border-black shadow-lg shadow-red-500/50" />
            </button>
            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-blue-500 to-purple-600 p-[2px] cursor-pointer hover:scale-105 active:scale-95 transition-all shadow-lg shadow-blue-500/30 hover:shadow-blue-500/50 group">
               <div className="w-full h-full rounded-full bg-black/80 flex items-center justify-center group-hover:bg-black/60 transition-colors">
                  <User className="w-5 h-5 text-gray-300" />
               </div>
            </div>
          </div>
        </header>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-8 custom-scrollbar">
          <section className="mb-12">
            <div className="flex items-end justify-between mb-8">
              <div>
                <h2 className="text-5xl font-extrabold tracking-tight mb-3 gradient-text">Library</h2>
                <div className="h-1.5 w-12 bg-blue-500 rounded-full mb-2"></div>
                <p className="text-gray-400 font-medium">Your personal collection, curated by you.</p>
              </div>
              <button className="flex items-center gap-2 px-4 py-2 rounded-xl text-xs font-bold tracking-widest text-gray-400 hover:text-white hover:bg-white/5 transition-all border border-white/5 uppercase">
                <Filter className="w-3.5 h-3.5" />
                Sort: Recent
              </button>
            </div>

            <MediaGrid 
              items={filteredData} 
              onSelect={(item) => setSelectedMedia(item)} 
            />
          </section>

          {/* Empty State */}
          {filteredData.length === 0 && (
            <div className="flex flex-col items-center justify-center py-20 text-center">
              <div className="w-20 h-20 bg-white/5 rounded-full flex items-center justify-center mb-6">
                <Search className="w-10 h-10 text-gray-700" />
              </div>
              <h3 className="text-xl font-bold mb-2">No results found</h3>
              <p className="text-gray-500">Try searching for something else or check your filters.</p>
            </div>
          )}
        </div>

        {/* Global Player */}
        <Player 
          item={selectedMedia} 
          onClose={() => setSelectedMedia(null)} 
        />
      </main>
    </div>
  );
}

export default App;

