const apiKey = import.meta.env.VITE_SELFDB_KEY || '';

export const selfdb = new SelfDB({
  baseUrl,
  apiKey,
});

export const TABLES = {
  MEDIA: 'media'
};

export const BUCKETS = {
  MEDIA: 'media_files'
};

