  const [searchQuery, setSearchQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const fetchMedia = async () => {
      try {
        setIsLoading(true);
        // List table records from SelfDB
        const response = await selfdb.tables.data.fetch(TABLES.MEDIA, {
