import React, { useState, useRef } from 'react';
import { Upload, X, FileAudio, FileVideo, Plus, CheckCircle2, AlertCircle, Loader2 } from 'lucide-react';
import { selfdb } from '../lib/selfdb';

interface UploadModalProps {
  isOpen: boolean;
  onClose: () => void;
  onUploadComplete: () => void;
}

export const UploadModal = ({ isOpen, onClose, onUploadComplete }: UploadModalProps) => {
  const [isDragging, setIsDragging] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState<{ [key: string]: number }>({});
  const [error, setError] = useState<string | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  if (!isOpen) return null;

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const files = Array.from(e.dataTransfer.files);
    if (files.length > 0) {
      handleUpload(files);
    }
  };

  const handleFileInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files ? Array.from(e.target.files) : [];
    if (files.length > 0) {
      handleUpload(files);
    }
  };

  const handleUpload = async (files: File[]) => {
    setIsUploading(true);
    setError(null);

    try {
      for (const file of files) {
        const isVideo = file.type.startsWith('video/');
        const isAudio = file.type.startsWith('audio/');

        if (!isVideo && !isAudio) {
          throw new Error(`File ${file.name} is not a supported audio or video file.`);
        }

        const bucketId = await selfdb.getBucketId('media-files');
        if (!bucketId) throw new Error('Bucket "media-files" not found. Please create it first.');

        // 1. Upload file to SelfDB Storage
        const { id: fileId } = await selfdb.storage.upload(bucketId, file);

        // 2. Extract basic metadata (Title from filename)
        const title = file.name.replace(/\.[^/.]+$/, "").replace(/[_-]/g, ' ');

        // 3. Create record in media table
        // We use the direct storage link format as recommended by SelfDB
        const fileUrl = `${selfdb.config.dbUrl}/v1/storage/file/${fileId}?key=${selfdb.config.apiKey}`;

        await selfdb.db.table('media').insert({
          title: title,
          artist: 'Unknown Artist',
          type: isVideo ? 'video' : 'music',
          duration: '0:00', // In a real app, we'd extract this from the file metadata
          cover: isVideo 
            ? 'https://images.unsplash.com/photo-1536440136628-849c177e76a1?w=800&auto=format&fit=crop&q=60' 
            : 'https://images.unsplash.com/photo-1614613535308-eb5fbd3d2c17?w=800&auto=format&fit=crop&q=60',
          url: fileUrl,
          category: isVideo ? 'Movies' : 'Playlists'
        });
      }

      onUploadComplete();
      onClose();
    } catch (err: any) {
      console.error('Upload error:', err);
      setError(err.message || 'Failed to upload files');
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/80 backdrop-blur-md">
      <div className="relative w-full max-w-xl bg-[#0a0a0b] border border-white/10 rounded-2xl shadow-2xl overflow-hidden elevation-3 animate-in fade-in zoom-in duration-200">

        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-white/5">
          <h2 className="text-xl font-bold text-white tracking-tight">Add Media</h2>
          <button 
            onClick={onClose}
            className="p-2 rounded-xl hover:bg-white/5 border border-transparent hover:border-white/10 transition-all active:scale-95 group"
          >
            <X className="w-5 h-5 text-gray-400 group-hover:text-red-400" />
          </button>
        </div>

        <div className="p-8">
          {error && (
            <div className="mb-6 p-4 rounded-xl bg-red-500/10 border border-red-500/20 flex items-center gap-3 text-red-400 text-sm">
              <AlertCircle className="w-5 h-5 flex-shrink-0" />
              <p>{error}</p>
            </div>
          )}

          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={() => fileInputRef.current?.click()}
            className={`
              relative flex flex-col items-center justify-center p-12 border-2 border-dashed rounded-3xl transition-all cursor-pointer group
              ${isDragging 
                ? 'border-blue-500 bg-blue-500/10 scale-[1.02]' 
                : 'border-white/10 hover:border-blue-500/50 hover:bg-white/5'}
            `}
          >
            <input 
              type="file" 
              ref={fileInputRef} 
              onChange={handleFileInput} 
              multiple 
              className="hidden" 
              accept="audio/*,video/*"
            />

            <div className={`
              w-16 h-16 mb-6 rounded-2xl flex items-center justify-center transition-all duration-300
              ${isDragging ? 'bg-blue-500 text-white shadow-xl shadow-blue-500/40' : 'bg-white/5 text-gray-400 group-hover:text-blue-400 group-hover:bg-blue-500/10'}
            `}>
              <Upload className={`w-8 h-8 ${isDragging ? 'animate-bounce' : ''}`} />
            </div>

            <div className="text-center">
              <p className="text-lg font-semibold text-white mb-1">
                {isDragging ? 'Drop to upload' : 'Drag & drop media files'}
              </p>
              <p className="text-sm text-gray-400">
                Support for MP3, WAV, MP4, MKV and more
              </p>
            </div>

            {isUploading && (
              <div className="absolute inset-0 bg-[#0a0a0b]/80 backdrop-blur-sm flex flex-col items-center justify-center rounded-3xl">
                <Loader2 className="w-10 h-10 text-blue-500 animate-spin mb-4" />
                <p className="text-white font-medium">Uploading your media...</p>
                <p className="text-sm text-gray-400 mt-1">This may take a moment</p>
              </div>
            )}
          </div>
        </div>

        {/* Footer info */}
        <div className="px-8 pb-8 flex items-center justify-center gap-6 text-xs text-gray-500">
          <div className="flex items-center gap-1.5">
            <FileAudio className="w-3.5 h-3.5" />
            <span>High Quality Audio</span>
          </div>
          <div className="flex items-center gap-1.5">
            <FileVideo className="w-3.5 h-3.5" />
            <span>4K Video Support</span>
          </div>
          <div className="flex items-center gap-1.5">
            <CheckCircle2 className="w-3.5 h-3.5 text-blue-500" />
            <span>Synced to Cloud</span>
          </div>
        </div>
      </div>
    </div>
  );
};

