import { Play, Pause, SkipBack, SkipForward, Volume2, X, Repeat, Shuffle } from 'lucide-react';
import { motion } from 'framer-motion';
import { selfdb, BUCKETS } from '../lib/selfdb';

interface MediaItem {
  id: string;
  title: string;
  artist: string;
  type: 'audio' | 'video';
  thumbnail: string;
  url: string;
}

interface PlayerProps {
  item: MediaItem | null;
  onClose: () => void;
}

export const Player = ({ item, onClose }: PlayerProps) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const audioRef = useRef<HTMLAudioElement>(null);
  const [isPlaying, setIsPlaying] = useState(true);
  const [progress, setProgress] = useState(0);
  const [mediaUrl, setMediaUrl] = useState<string>('');

  // Resolve the media URL - either direct URL or SelfDB storage path
  useEffect(() => {
    if (!item) {
      setMediaUrl('');
      return;
    }

    // If it's already a full URL (http/https), use it directly
    if (item.url.startsWith('http://') || item.url.startsWith('https://')) {
      setMediaUrl(item.url);
      return;
    }

    // Otherwise, it's a SelfDB storage path - construct public URL
    // Format: {baseUrl}/storage/{bucketName}/{path}
    const baseUrl = import.meta.env.VITE_SELFDB_URL || 'http://localhost:8000';
    const publicUrl = `${baseUrl}/storage/${BUCKETS.MEDIA}/${item.url}`;
    setMediaUrl(publicUrl);
  }, [item]);

  useEffect(() => {
    setIsPlaying(true);
    setProgress(0);
  }, [item]);

