import { useState, useMemo, useEffect } from 'react';
import { Sidebar } from './components/Sidebar';
import { MediaGrid } from './components/MediaGrid';
import { Player } from './components/Player';
import { Search, Bell, User, Filter } from 'lucide-react';
import { selfdb, TABLES, getTableId } from './lib/selfdb';
import localData from './data.json';

function App() {
  const [mediaData, setMediaData] = useState<any[]>(localData);
  const [selectedMedia, setSelectedMedia] = useState<any>(null);
  const [filter, setFilter] = useState<'all' | 'audio' | 'video'>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const fetchMedia = async () => {
    try {
      setIsLoading(true);

      // Get table ID dynamically as required by SelfDB SDK
      const tableId = await getTableId(TABLES.MEDIA);

      // List table records from SelfDB using query builder for better control
      const response = await selfdb.tables.data
        .query(tableId)
        .pageSize(100)
        .execute();

      if (response && response.data && response.data.length > 0) {
        setMediaData(response.data);
      }
    } catch (error) {
      console.error('Error fetching from SelfDB, falling back to local data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchMedia();
  }, []);

  const filteredData = useMemo(() => {
    return mediaData.filter((item) => {
      const matchesFilter = filter === 'all' || item.type === filter;
      const matchesSearch = (item.title?.toLowerCase().includes(searchQuery.toLowerCase()) || 
                           item.artist?.toLowerCase().includes(searchQuery.toLowerCase()));
      return matchesFilter && matchesSearch;
    });
  }, [mediaData, filter, searchQuery]);

  return (
    <div className="flex h-screen bg-background text-foreground overflow-hidden font-sans mesh-gradient">
      <Sidebar onMediaAdded={fetchMedia} />

      <main className="flex-1 flex flex-col min-w-0 relative">
        {/* Header */}
        <header className="h-20 flex items-center justify-between px-8 bg-black/30 backdrop-blur-2xl z-10 border-b border-white/5 shadow-[0_4px_20px_rgba(0,0,0,0.3)]">
          <div className="flex items-center gap-4 glass px-5 py-3 rounded-2xl w-full max-w-md focus-within:shadow-[0_0_30px_rgba(59,130,246,0.2)] focus-within:border-blue-500/30 transition-all duration-300">
            <Search className="w-5 h-5 text-gray-500" />
            <input 
              type="text" 
              placeholder="Search music, videos, artists..." 
              className="bg-transparent border-none outline-none w-full text-sm py-1 placeholder:text-gray-600"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>

          <div className="flex items-center gap-6 ml-6">
            <div className="hidden sm:flex items-center p-1 glass rounded-2xl">
              <button 
                onClick={() => setFilter('all')}
                className={`px-5 py-1.5 rounded-xl text-sm font-semibold transition-all duration-300 ${filter === 'all' ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-lg shadow-blue-500/40' : 'text-gray-400 hover:text-white'}`}
              >
                All
              </button>
              <button 
                onClick={() => setFilter('audio')}
                className={`px-5 py-1.5 rounded-xl text-sm font-semibold transition-all duration-300 ${filter === 'audio' ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-lg shadow-blue-500/40' : 'text-gray-400 hover:text-white'}`}
              >
                Music
              </button>
              <button 
                onClick={() => setFilter('video')}
                className={`px-5 py-1.5 rounded-xl text-sm font-semibold transition-all duration-300 ${filter === 'video' ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-lg shadow-blue-500/40' : 'text-gray-400 hover:text-white'}`}
              >
                Videos
              </button>
            </div>
            <div className="w-[1px] h-6 bg-white/10 hidden sm:block" />
            <button className="relative p-2.5 rounded-xl bg-white/5 hover:bg-white/10 hover:border-white/10 transition-all border border-white/5 group">
              <Bell className="w-5 h-5 text-gray-400 group-hover:text-white transition-colors" />
              <span className="absolute top-2 right-2 w-2.5 h-2.5 bg-red-500 rounded-full border-2 border-black shadow-lg shadow-red-500/50" />
            </button>
            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-blue-500 to-purple-600 p-[2px] cursor-pointer hover:scale-105 active:scale-95 transition-all shadow-lg shadow-blue-500/30 hover:shadow-blue-500/50 group">
               <div className="w-full h-full rounded-full bg-black/80 flex items-center justify-center group-hover:bg-black/60 transition-colors">
                  <User className="w-5 h-5 text-gray-300" />
               </div>
            </div>
          </div>
        </header>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-8 custom-scrollbar">
          <section className="mb-12">
            <div className="flex items-end justify-between mb-8">
              <div>
                <h2 className="text-5xl font-extrabold tracking-tight mb-3 gradient-text">Library</h2>
                <div className="h-1.5 w-12 bg-blue-500 rounded-full mb-2"></div>
                <p className="text-gray-400 font-medium">Your personal collection, curated by you.</p>
              </div>
              <button className="flex items-center gap-2 px-4 py-2 rounded-xl text-xs font-bold tracking-widest text-gray-400 hover:text-white hover:bg-white/5 transition-all border border-white/5 uppercase">
                <Filter className="w-3.5 h-3.5" />
                Sort: Recent
              </button>
            </div>

            <MediaGrid 
              items={filteredData} 
              onSelect={(item) => setSelectedMedia(item)} 
            />
            {isLoading && (
              <div className="flex justify-center mt-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
              </div>
            )}
          </section>

          {/* Empty State */}
          {filteredData.length === 0 && (
            <div className="flex flex-col items-center justify-center py-20 text-center">
              <div className="w-20 h-20 bg-white/5 rounded-full flex items-center justify-center mb-6">
                <Search className="w-10 h-10 text-gray-700" />
              </div>
              <h3 className="text-xl font-bold mb-2">No results found</h3>
              <p className="text-gray-500">Try searching for something else or check your filters.</p>
            </div>
          )}
        </div>

        {/* Global Player */}
        <Player 
          item={selectedMedia} 
          onClose={() => setSelectedMedia(null)} 
        />
      </main>
    </div>
  );
}

export default App;

import React, { useState, useRef } from 'react';
import { Upload, X, FileAudio, FileVideo, CheckCircle2, AlertCircle, Loader2 } from 'lucide-react';
import { selfdb, getBucketId, getTableId, TABLES, BUCKETS } from '../lib/selfdb';

interface UploadModalProps {
  isOpen: boolean;
  onClose: () => void;
  onUploadComplete: () => void;
}

export const UploadModal = ({ isOpen, onClose, onUploadComplete }: UploadModalProps) => {
  const [isDragging, setIsDragging] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState<{ [key: string]: number }>({});
  const [error, setError] = useState<string | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  if (!isOpen) return null;

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const files = Array.from(e.dataTransfer.files);
    if (files.length > 0) {
      handleUpload(files);
    }
  };

  const handleFileInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files ? Array.from(e.target.files) : [];
    if (files.length > 0) {
      handleUpload(files);
    }
  };

  const fileToArrayBuffer = (file: File): Promise<ArrayBuffer> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result as ArrayBuffer);
      reader.onerror = reject;
      reader.readAsArrayBuffer(file);
    });
  };

  const handleUpload = async (files: File[]) => {
    setIsUploading(true);
    setError(null);

    try {
      // Get bucket and table IDs first
      const bucketId = await getBucketId(BUCKETS.MEDIA);
      const tableId = await getTableId(TABLES.MEDIA);

      if (!bucketId) throw new Error(`Bucket "${BUCKETS.MEDIA}" not found. Please create it first.`);
      if (!tableId) throw new Error(`Table "${TABLES.MEDIA}" not found. Please create it first.`);

      for (const file of files) {
        const isVideo = file.type.startsWith('video/');
        const isAudio = file.type.startsWith('audio/');

        if (!isVideo && !isAudio) {
          throw new Error(`File ${file.name} is not a supported audio or video file.`);
        }

        setUploadProgress(prev => ({ ...prev, [file.name]: 0 }));

        // Convert file to ArrayBuffer for upload
        const fileData = await fileToArrayBuffer(file);

        // 1. Upload file to SelfDB Storage
        const uploadResult = await selfdb.storage.files.upload(bucketId, {
          filename: file.name,
          data: fileData,
          contentType: file.type,
          path: isAudio ? 'audio' : 'video'
        });

        setUploadProgress(prev => ({ ...prev, [file.name]: 100 }));

        // 2. Extract basic metadata (Title from filename)
        const title = file.name.replace(/\.[^/.]+$/, "").replace(/[_-]/g, ' ');

        // 3. Create record in media table
        // The file URL will be constructed from the bucket name and path
        const filePath = uploadResult.path || `${isAudio ? 'audio' : 'video'}/${file.name}`;

        await selfdb.tables.data.insert(tableId, {
          id: crypto.randomUUID(),
          title: title,
          artist: 'Unknown Artist',
          type: isAudio ? 'audio' : 'video',
          duration: '0:00',
          cover: isVideo 
            ? 'https://images.unsplash.com/photo-1536440136628-849c177e76a1?w=800&auto=format&fit=crop&q=60' 
            : 'https://images.unsplash.com/photo-1614613535308-eb5fbd3d2c17?w=800&auto=format&fit=crop&q=60',
          url: filePath
        });
      }

      onUploadComplete();
      onClose();
    } catch (err: any) {
      console.error('Upload error:', err);
      setError(err.message || 'Failed to upload files. Make sure SelfDB is configured correctly.');
    } finally {
      setIsUploading(false);
      setUploadProgress({});
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/80 backdrop-blur-md">
      <div className="relative w-full max-w-xl bg-[#0a0a0b] border border-white/10 rounded-2xl shadow-2xl overflow-hidden elevation-3 animate-in fade-in zoom-in duration-200">

        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-white/5">
          <h2 className="text-xl font-bold text-white tracking-tight">Add Media</h2>
          <button 
            onClick={onClose}
            disabled={isUploading}
            className="p-2 rounded-xl hover:bg-white/5 border border-transparent hover:border-white/10 transition-all active:scale-95 group disabled:opacity-50"
          >
            <X className="w-5 h-5 text-gray-400 group-hover:text-red-400" />
          </button>
        </div>

        <div className="p-8">
          {error && (
            <div className="mb-6 p-4 rounded-xl bg-red-500/10 border border-red-500/20 flex items-center gap-3 text-red-400 text-sm">
              <AlertCircle className="w-5 h-5 flex-shrink-0" />
              <p>{error}</p>
            </div>
          )}

          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={() => !isUploading && fileInputRef.current?.click()}
            className={`
              relative flex flex-col items-center justify-center p-12 border-2 border-dashed rounded-3xl transition-all cursor-pointer group
              ${isDragging 
                ? 'border-blue-500 bg-blue-500/10 scale-[1.02]' 
                : 'border-white/10 hover:border-blue-500/50 hover:bg-white/5'}
              ${isUploading ? 'pointer-events-none opacity-50' : ''}
            `}
          >
            <input 
              type="file" 
              ref={fileInputRef} 
              onChange={handleFileInput} 
              multiple 
              className="hidden" 
              accept="audio/*,video/*"
              disabled={isUploading}
            />

            <div className={`
              w-16 h-16 mb-6 rounded-2xl flex items-center justify-center transition-all duration-300
              ${isDragging ? 'bg-blue-500 text-white shadow-xl shadow-blue-500/40' : 'bg-white/5 text-gray-400 group-hover:text-blue-400 group-hover:bg-blue-500/10'}
            `}>
              <Upload className={`w-8 h-8 ${isDragging ? 'animate-bounce' : ''}`} />
            </div>

            <div className="text-center">
              <p className="text-lg font-semibold text-white mb-1">
                {isDragging ? 'Drop to upload' : 'Drag & drop media files'}
              </p>
              <p className="text-sm text-gray-400">
                Support for MP3, WAV, MP4, MKV and more
              </p>
            </div>

            {isUploading && (
              <div className="absolute inset-0 bg-[#0a0a0b]/90 backdrop-blur-sm flex flex-col items-center justify-center rounded-3xl">
                <Loader2 className="w-10 h-10 text-blue-500 animate-spin mb-4" />
                <p className="text-white font-medium">Uploading your media...</p>
                <p className="text-sm text-gray-400 mt-1">This may take a moment</p>
                {Object.keys(uploadProgress).length > 0 && (
                  <div className="mt-4 w-64">
                    {Object.entries(uploadProgress).map(([filename, progress]) => (
                      <div key={filename} className="mb-2">
                        <p className="text-xs text-gray-500 truncate mb-1">{filename}</p>
                        <div className="h-1 bg-white/10 rounded-full overflow-hidden">
                          <div 
                            className="h-full bg-blue-500 transition-all duration-300"
                            style={{ width: `${progress}%` }}
                          />
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Footer info */}
        <div className="px-8 pb-8 flex items-center justify-center gap-6 text-xs text-gray-500">
          <div className="flex items-center gap-1.5">
            <FileAudio className="w-3.5 h-3.5" />
            <span>High Quality Audio</span>
          </div>
          <div className="flex items-center gap-1.5">
            <FileVideo className="w-3.5 h-3.5" />
            <span>4K Video Support</span>
          </div>
          <div className="flex items-center gap-1.5">
            <CheckCircle2 className="w-3.5 h-3.5 text-blue-500" />
            <span>Synced to SelfDB</span>
          </div>
        </div>
      </div>
    </div>
  );
