import { useRef, useEffect, useState } from 'react';
import { Play, Pause, SkipBack, SkipForward, Volume2, X, Repeat, Shuffle } from 'lucide-react';
import { motion } from 'framer-motion';
import { BUCKETS } from '../lib/selfdb';

interface MediaItem {
  id: string;
  title: string;
  artist: string;
  type: 'audio' | 'video';
  cover: string;
  url: string;
}

interface PlayerProps {
  item: MediaItem | null;
  onClose: () => void;
}

export const Player = ({ item, onClose }: PlayerProps) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const audioRef = useRef<HTMLAudioElement>(null);
  const [isPlaying, setIsPlaying] = useState(true);
  const [progress, setProgress] = useState(0);
  const [mediaUrl, setMediaUrl] = useState<string>('');

  // Resolve the media URL - either direct URL or SelfDB storage path
  useEffect(() => {
    if (!item) {
      setMediaUrl('');
      return;
    }

    // If it's already a full URL (http/https), use it directly
    if (item.url.startsWith('http://') || item.url.startsWith('https://')) {
      setMediaUrl(item.url);
      return;
    }

    // Otherwise, it's a SelfDB storage path - construct public URL
    // Format: {baseUrl}/storage/{bucketName}/{path}
    const baseUrl = import.meta.env.VITE_SELFDB_URL || 'http://localhost:8000';
    const publicUrl = `${baseUrl}/storage/${BUCKETS.MEDIA}/${item.url}`;
    setMediaUrl(publicUrl);
  }, [item]);

  useEffect(() => {
    setIsPlaying(true);
    setProgress(0);
  }, [item]);

  const togglePlay = () => {
    const media = item?.type === 'video' ? videoRef.current : audioRef.current;
    if (media) {
      if (isPlaying) {
        media.pause();
      } else {
        media.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleTimeUpdate = (e: any) => {
    const { currentTime, duration } = e.target;
    setProgress((currentTime / duration) * 100);
  };

  if (!item) return null;

  return (
    <motion.div 
      initial={{ y: 100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      className="fixed bottom-0 left-0 right-0 z-50 px-4 pb-4 md:px-10 md:pb-10 pointer-events-none"
    >
      <div className="relative max-w-7xl mx-auto bg-gradient-to-r from-white/10 to-white/5 backdrop-blur-2xl border border-white/10 rounded-3xl p-6 flex flex-col md:flex-row items-center gap-6 pointer-events-auto shadow-[0_20px_60px_rgba(59,130,246,0.15)] elevated">

        {/* Close Button - Positioned in top right corner */}
        <button 
          onClick={onClose} 
          className="absolute top-4 right-4 p-2 bg-white/5 hover:bg-red-500/10 border border-white/10 hover:border-red-500/50 rounded-xl transition-all group active:scale-90 shadow-sm hover:shadow-red-500/20 z-10"
          aria-label="Close player"
        >
          <X className="w-4 h-4 text-gray-400 group-hover:text-red-500 transition-colors" />
          <div className="absolute inset-0 rounded-xl bg-red-500/0 group-hover:bg-red-500/5 blur-md transition-all" />
        </button>

        {/* Thumbnail/Video Area */}
        <div className="relative w-20 h-20 md:w-24 md:h-24 rounded-2xl overflow-hidden flex-shrink-0 bg-black">
          {item.type === 'video' ? (
            <video 
              ref={videoRef}
              src={mediaUrl}
              autoPlay
              onTimeUpdate={handleTimeUpdate}
              className="w-full h-full object-cover"
              onClick={togglePlay}
            />
          ) : (
            <>
              <img src={item.cover} className="w-full h-full object-cover opacity-50" />
              <audio ref={audioRef} src={mediaUrl} autoPlay onTimeUpdate={handleTimeUpdate} />
              <div className="absolute inset-0 flex items-center justify-center">
                 <div className="w-8 h-8 rounded-full border-2 border-accent border-t-transparent animate-spin" />
              </div>
            </>
          )}
        </div>

        {/* Info Area */}
        <div className="flex-1 min-w-0 flex flex-col items-center md:items-start pr-4">
          <h4 className="font-bold text-lg truncate w-full text-center md:text-left">{item.title}</h4>
          <p className="text-sm text-gray-400 truncate w-full text-center md:text-left">{item.artist}</p>
        </div>

        {/* Controls */}
        <div className="flex flex-col items-center gap-3 w-full md:w-1/3">
          <div className="flex items-center gap-6">
            <button className="text-gray-500 hover:text-white hover:bg-white/5 p-2 rounded-lg transition-all"><Shuffle className="w-4 h-4" /></button>
            <button className="text-gray-500 hover:text-white hover:bg-white/5 p-2 rounded-lg transition-all"><SkipBack className="w-5 h-5 fill-current" /></button>
            <button 
              onClick={togglePlay}
              className="w-14 h-14 bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-full flex items-center justify-center hover:scale-110 active:scale-95 transition-all shadow-lg shadow-blue-500/50 glow-accent"
            >
              {isPlaying ? <Pause className="w-7 h-7 fill-current" /> : <Play className="w-7 h-7 fill-current ml-1" />}
            </button>
            <button className="text-gray-500 hover:text-white hover:bg-white/5 p-2 rounded-lg transition-all"><SkipForward className="w-5 h-5 fill-current" /></button>
            <button className="text-gray-500 hover:text-white hover:bg-white/5 p-2 rounded-lg transition-all"><Repeat className="w-4 h-4" /></button>
          </div>

          <div className="w-full h-1.5 bg-white/5 rounded-full overflow-hidden backdrop-blur-sm">
            <motion.div 
               className="h-full bg-gradient-to-r from-blue-500 to-blue-400 rounded-full"
               animate={{ width: `${progress}%` }}
               transition={{ type: 'spring', bounce: 0, duration: 0.1 }}
            />
          </div>
        </div>

        {/* Action area */}
        <div className="flex items-center gap-5 w-full md:w-48 justify-center md:justify-end">
          <div className="hidden md:flex items-center gap-5 flex-1 justify-end">
            <Volume2 className="w-5 h-5 text-gray-400 group hover:text-white transition-colors" />
            <div className="w-24 h-1.5 bg-white/5 rounded-full overflow-hidden">
              <div className="w-2/3 h-full bg-gradient-to-r from-blue-500 to-blue-400 rounded-full shadow-lg shadow-blue-500/30" />
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

# SelfDB JS SDK  Complete API Reference

## Client Initialization

```typescript
import { SelfDB } from '@selfdb/js-sdk';

const selfdb = new SelfDB({
  baseUrl: string,   // Required: API base URL (e.g., 'https://api.selfdb.io')
  apiKey: string,    // Required: API key from dashboard
  timeout?: number   // Optional: request timeout in milliseconds
});
```

