import React, { useState, useRef } from 'react';
import { Upload, X, FileAudio, FileVideo, CheckCircle2, AlertCircle, Loader2 } from 'lucide-react';
import { selfdb, getBucketId, getTableId, TABLES, BUCKETS } from '../lib/selfdb';

interface UploadModalProps {
  isOpen: boolean;
  onClose: () => void;
  onUploadComplete: () => void;
}

export const UploadModal = ({ isOpen, onClose, onUploadComplete }: UploadModalProps) => {
  const [isDragging, setIsDragging] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState<{ [key: string]: number }>({});
  const [error, setError] = useState<string | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  if (!isOpen) return null;

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const files = Array.from(e.dataTransfer.files);
    if (files.length > 0) {
      handleUpload(files);
    }
  };

  const handleFileInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files ? Array.from(e.target.files) : [];
    if (files.length > 0) {
      handleUpload(files);
    }
  };

  const fileToArrayBuffer = (file: File): Promise<ArrayBuffer> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result as ArrayBuffer);
      reader.onerror = reject;
      reader.readAsArrayBuffer(file);
    });
  };

  const handleUpload = async (files: File[]) => {
    setIsUploading(true);
    setError(null);

    try {
      // Get bucket and table IDs first
      const bucketId = await getBucketId(BUCKETS.MEDIA);
      const tableId = await getTableId(TABLES.MEDIA);

      if (!bucketId) throw new Error(`Bucket "${BUCKETS.MEDIA}" not found. Please create it first.`);
      if (!tableId) throw new Error(`Table "${TABLES.MEDIA}" not found. Please create it first.`);

      for (const file of files) {
        const isVideo = file.type.startsWith('video/');
        const isAudio = file.type.startsWith('audio/');

        if (!isVideo && !isAudio) {
          throw new Error(`File ${file.name} is not a supported audio or video file.`);
        }

        setUploadProgress(prev => ({ ...prev, [file.name]: 0 }));

        // Convert file to ArrayBuffer for upload
        const fileData = await fileToArrayBuffer(file);

        setUploadProgress(prev => ({ ...prev, [file.name]: 30 }));

        // 1. Upload file to SelfDB Storage
        // Store in subdirectories: audio/ or video/
        const subdir = isAudio ? 'audio' : 'video';

        const uploadResult = await selfdb.storage.files.upload(bucketId, {
          filename: file.name,
          data: fileData,
          contentType: file.type,
          path: subdir  // This creates audio/filename.mp3 or video/filename.mp4
        });

        console.log('Upload result:', uploadResult);
        setUploadProgress(prev => ({ ...prev, [file.name]: 80 }));

        // 2. Extract basic metadata (Title from filename)
        const title = file.name.replace(/\.[^/.]+$/, "").replace(/[_-]/g, ' ');

        // 3. Create record in media table
        // Store the full path: audio/filename.mp3 or video/filename.mp4
        const filePath = uploadResult.path || `${subdir}/${file.name}`;

        await selfdb.tables.data.insert(tableId, {
          id: crypto.randomUUID(),
          title: title,
          artist: 'Unknown Artist',
          type: isAudio ? 'audio' : 'video',
          duration: '0:00',
          cover: isVideo 
            ? 'https://images.unsplash.com/photo-1536440136628-849c177e76a1?w=800&auto=format&fit=crop&q=60' 
            : 'https://images.unsplash.com/photo-1614613535308-eb5fbd3d2c17?w=800&auto=format&fit=crop&q=60',
          url: filePath
        });

        setUploadProgress(prev => ({ ...prev, [file.name]: 100 }));
      }

      onUploadComplete();
      onClose();
    } catch (err: any) {
      console.error('Upload error:', err);
      setError(err.message || 'Failed to upload files. Make sure SelfDB is configured correctly.');
    } finally {
      setIsUploading(false);
      setUploadProgress({});
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/80 backdrop-blur-md">
      <div className="relative w-full max-w-xl bg-[#0a0a0b] border border-white/10 rounded-2xl shadow-2xl overflow-hidden elevation-3 animate-in fade-in zoom-in duration-200">

        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-white/5">
          <h2 className="text-xl font-bold text-white tracking-tight">Add Media</h2>
          <button 
            onClick={onClose}
            disabled={isUploading}
            className="p-2 rounded-xl hover:bg-white/5 border border-transparent hover:border-white/10 transition-all active:scale-95 group disabled:opacity-50"
          >
            <X className="w-5 h-5 text-gray-400 group-hover:text-red-400" />
          </button>
        </div>

        <div className="p-8">
          {error && (
            <div className="mb-6 p-4 rounded-xl bg-red-500/10 border border-red-500/20 flex items-center gap-3 text-red-400 text-sm">
              <AlertCircle className="w-5 h-5 flex-shrink-0" />
              <p>{error}</p>
            </div>
          )}

          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={() => !isUploading && fileInputRef.current?.click()}
            className={`
              relative flex flex-col items-center justify-center p-12 border-2 border-dashed rounded-3xl transition-all cursor-pointer group
              ${isDragging 
                ? 'border-blue-500 bg-blue-500/10 scale-[1.02]' 
                : 'border-white/10 hover:border-blue-500/50 hover:bg-white/5'}
              ${isUploading ? 'pointer-events-none opacity-50' : ''}
            `}
          >
            <input 
              type="file" 
              ref={fileInputRef} 
              onChange={handleFileInput} 
              multiple 
              className="hidden" 
              accept="audio/*,video/*"
              disabled={isUploading}
            />

            <div className={`
              w-16 h-16 mb-6 rounded-2xl flex items-center justify-center transition-all duration-300
              ${isDragging ? 'bg-blue-500 text-white shadow-xl shadow-blue-500/40' : 'bg-white/5 text-gray-400 group-hover:text-blue-400 group-hover:bg-blue-500/10'}
            `}>
              <Upload className={`w-8 h-8 ${isDragging ? 'animate-bounce' : ''}`} />
            </div>

            <div className="text-center">
              <p className="text-lg font-semibold text-white mb-1">
                {isDragging ? 'Drop to upload' : 'Drag & drop media files'}
              </p>
              <p className="text-sm text-gray-400">
                Support for MP3, WAV, MP4, MKV and more
              </p>
            </div>

            {isUploading && (
              <div className="absolute inset-0 bg-[#0a0a0b]/90 backdrop-blur-sm flex flex-col items-center justify-center rounded-3xl">
                <Loader2 className="w-10 h-10 text-blue-500 animate-spin mb-4" />
                <p className="text-white font-medium">Uploading your media...</p>
                <p className="text-sm text-gray-400 mt-1">This may take a moment</p>
                {Object.keys(uploadProgress).length > 0 && (
                  <div className="mt-4 w-64">
                    {Object.entries(uploadProgress).map(([filename, progress]) => (
                      <div key={filename} className="mb-2">
                        <p className="text-xs text-gray-500 truncate mb-1">{filename}</p>
                        <div className="h-1 bg-white/10 rounded-full overflow-hidden">
                          <div 
                            className="h-full bg-blue-500 transition-all duration-300"
                            style={{ width: `${progress}%` }}
                          />
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Footer info */}
        <div className="px-8 pb-8 flex items-center justify-center gap-6 text-xs text-gray-500">
          <div className="flex items-center gap-1.5">
            <FileAudio className="w-3.5 h-3.5" />
            <span>High Quality Audio</span>
          </div>
          <div className="flex items-center gap-1.5">
            <FileVideo className="w-3.5 h-3.5" />
            <span>4K Video Support</span>
          </div>
          <div className="flex items-center gap-1.5">
            <CheckCircle2 className="w-3.5 h-3.5 text-blue-500" />
            <span>Synced to SelfDB</span>
          </div>
        </div>
      </div>
    </div>
  );
};

import { useRef, useEffect, useState, useCallback } from 'react';
import { Play, Pause, SkipBack, SkipForward, Volume2, X, Repeat, Shuffle, Loader2 } from 'lucide-react';
import { motion } from 'framer-motion';
import { selfdb, BUCKETS } from '../lib/selfdb';

interface MediaItem {
  id: string;
  title: string;
  artist: string;
  type: 'audio' | 'video';
  cover: string;
  url: string;
}

interface PlayerProps {
  item: MediaItem | null;
  onClose: () => void;
}

export const Player = ({ item, onClose }: PlayerProps) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const audioRef = useRef<HTMLAudioElement>(null);
  const [isPlaying, setIsPlaying] = useState(true);
  const [progress, setProgress] = useState(0);
  const [mediaUrl, setMediaUrl] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [mimeType, setMimeType] = useState<string>('');

  // Download file from SelfDB and create blob URL
  const downloadAndPlay = useCallback(async (mediaItem: MediaItem) => {
    setIsLoading(true);
    setError(null);
    setMimeType('');

    try {
      let blobUrl: string;
      let detectedMimeType = '';

      // If it's already a full URL (http/https), use it directly
      if (mediaItem.url.startsWith('http://') || mediaItem.url.startsWith('https://')) {
        blobUrl = mediaItem.url;
        // Try to determine mime type from extension
        const ext = mediaItem.url.split('.').pop()?.toLowerCase();
        if (ext === 'mp3') detectedMimeType = 'audio/mpeg';
        else if (ext === 'wav') detectedMimeType = 'audio/wav';
        else if (ext === 'mp4') detectedMimeType = 'video/mp4';
        else if (ext === 'webm') detectedMimeType = 'video/webm';
      } else {
        // It's a SelfDB storage path - download as blob
        console.log('Downloading from SelfDB:', mediaItem.url);

        // Download file using SelfDB SDK
        const fileData = await selfdb.storage.files.download({
          bucketName: BUCKETS.MEDIA,
          path: mediaItem.url
        });

        console.log('File downloaded, size:', fileData.byteLength);

        // Determine mime type from file extension in the path
        const ext = mediaItem.url.split('.').pop()?.toLowerCase();
        if (ext === 'mp3') detectedMimeType = 'audio/mpeg';
        else if (ext === 'wav' || ext === 'wave') detectedMimeType = 'audio/wav';
        else if (ext === 'ogg') detectedMimeType = 'audio/ogg';
        else if (ext === 'flac') detectedMimeType = 'audio/flac';
        else if (ext === 'm4a') detectedMimeType = 'audio/mp4';
        else if (ext === 'mp4') detectedMimeType = 'video/mp4';
        else if (ext === 'webm') detectedMimeType = 'video/webm';
        else if (ext === 'mkv') detectedMimeType = 'video/x-matroska';
        else if (ext === 'mov') detectedMimeType = 'video/quicktime';
        else {
          // Default based on type
          detectedMimeType = mediaItem.type === 'audio' ? 'audio/mpeg' : 'video/mp4';
        }

        console.log('Detected MIME type:', detectedMimeType);
        setMimeType(detectedMimeType);

        // Create blob from ArrayBuffer with proper mime type
        const blob = new Blob([fileData], { type: detectedMimeType });
        blobUrl = URL.createObjectURL(blob);
        console.log('Blob URL created:', blobUrl);
      }

      setMediaUrl(blobUrl);
      setIsLoading(false);
    } catch (err: any) {
      console.error('Download error:', err);
      setError(err.message || 'Failed to load media file');
      setIsLoading(false);
    }
  }, []);

  // Cleanup blob URL when component unmounts or item changes
  useEffect(() => {
    return () => {
      if (mediaUrl && mediaUrl.startsWith('blob:')) {
        URL.revokeObjectURL(mediaUrl);
      }
    };
  }, [mediaUrl]);

  // Load media when item changes
  useEffect(() => {
    if (!item) {
      setMediaUrl('');
      setError(null);
      return;
    }

    setIsPlaying(true);
    setProgress(0);
    downloadAndPlay(item);
  }, [item, downloadAndPlay]);

  const togglePlay = () => {
    const media = item?.type === 'video' ? videoRef.current : audioRef.current;
    if (media) {
      if (isPlaying) {
        media.pause();
      } else {
        media.play().catch(err => {
          console.error('Play error:', err);
          setError('Failed to play media');
        });
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleTimeUpdate = (e: any) => {
    const { currentTime, duration } = e.target;
    if (duration) {
      setProgress((currentTime / duration) * 100);
    }
  };

  const handleLoadedMetadata = (e: any) => {
    console.log('Media loaded:', e.target.duration);
    setIsLoading(false);
  };

  const handleError = (e: any) => {
    console.error('Media error:', e);
    setError('Failed to load media. The file may be corrupted or in an unsupported format.');
    setIsLoading(false);
  };

  if (!item) return null;

  return (
    <motion.div 
      initial={{ y: 100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      className="fixed bottom-0 left-0 right-0 z-50 px-4 pb-4 md:px-10 md:pb-10 pointer-events-none"
    >
      <div className="relative max-w-7xl mx-auto bg-gradient-to-r from-white/10 to-white/5 backdrop-blur-2xl border border-white/10 rounded-3xl p-6 flex flex-col md:flex-row items-center gap-6 pointer-events-auto shadow-[0_20px_60px_rgba(59,130,246,0.15)] elevated">

        {/* Close Button - Positioned in top right corner */}
        <button 
          onClick={onClose} 
          className="absolute top-4 right-4 p-2 bg-white/5 hover:bg-red-500/10 border border-white/10 hover:border-red-500/50 rounded-xl transition-all group active:scale-90 shadow-sm hover:shadow-red-500/20 z-10"
          aria-label="Close player"
        >
          <X className="w-4 h-4 text-gray-400 group-hover:text-red-500 transition-colors" />
          <div className="absolute inset-0 rounded-xl bg-red-500/0 group-hover:bg-red-500/5 blur-md transition-all" />
        </button>

        {/* Thumbnail/Video Area */}
        <div className="relative w-20 h-20 md:w-24 md:h-24 rounded-2xl overflow-hidden flex-shrink-0 bg-black">
          {item.type === 'video' ? (
            <video 
              ref={videoRef}
              src={mediaUrl}
              autoPlay
              playsInline
              onTimeUpdate={handleTimeUpdate}
              onLoadedMetadata={handleLoadedMetadata}
              onError={handleError}
              className="w-full h-full object-cover"
              onClick={togglePlay}
            />
          ) : (
            <>
              <img 
                src={item.cover} 
                alt={item.title}
                className={`w-full h-full object-cover ${isPlaying && !isLoading ? 'opacity-50' : 'opacity-100'}`} 
              />
              {mediaUrl && (
                <audio 
                  ref={audioRef} 
                  src={mediaUrl} 
                  autoPlay 
                  onTimeUpdate={handleTimeUpdate}
                  onLoadedMetadata={handleLoadedMetadata}
                  onError={handleError}
                />
              )}

              {/* Loading Spinner */}
              {isLoading && (
                <div className="absolute inset-0 flex items-center justify-center bg-black/50">
                  <Loader2 className="w-8 h-8 text-blue-500 animate-spin" />
                </div>
              )}
            </>
          )}
        </div>

        {/* Info Area */}
        <div className="flex-1 min-w-0 flex flex-col items-center md:items-start pr-4">
          <h4 className="font-bold text-lg truncate w-full text-center md:text-left">{item.title}</h4>
          <p className="text-sm text-gray-400 truncate w-full text-center md:text-left">{item.artist}</p>
          {mimeType && (
            <p className="text-xs text-gray-500 mt-1">{mimeType}</p>
          )}
        </div>

        {/* Error Message */}
        {error && (
          <div className="absolute top-16 left-6 right-6 p-3 rounded-xl bg-red-500/10 border border-red-500/20 text-red-400 text-sm text-center">
            {error}
          </div>
        )}

        {/* Controls */}
        <div className="flex flex-col items-center gap-3 w-full md:w-1/3">
          <div className="flex items-center gap-6">
            <button className="text-gray-500 hover:text-white hover:bg-white/5 p-2 rounded-lg transition-all"><Shuffle className="w-4 h-4" /></button>
            <button className="text-gray-500 hover:text-white hover:bg-white/5 p-2 rounded-lg transition-all"><SkipBack className="w-5 h-5 fill-current" /></button>

            {/* Play/Pause Button */}
            <button 
              onClick={togglePlay}
              disabled={isLoading || !mediaUrl}
              className="w-14 h-14 bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-full flex items-center justify-center hover:scale-110 active:scale-95 transition-all shadow-lg shadow-blue-500/50 glow-accent disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isLoading ? (
                <Loader2 className="w-7 h-7 animate-spin" />
              ) : isPlaying ? (
                <Pause className="w-7 h-7 fill-current" />
              ) : (
                <Play className="w-7 h-7 fill-current ml-1" />
              )}
            </button>

            <button className="text-gray-500 hover:text-white hover:bg-white/5 p-2 rounded-lg transition-all"><SkipForward className="w-5 h-5 fill-current" /></button>
            <button className="text-gray-500 hover:text-white hover:bg-white/5 p-2 rounded-lg transition-all"><Repeat className="w-4 h-4" /></button>
          </div>

          <div className="w-full h-1.5 bg-white/5 rounded-full overflow-hidden backdrop-blur-sm">
            <motion.div 
               className="h-full bg-gradient-to-r from-blue-500 to-blue-400 rounded-full"
               animate={{ width: `${progress}%` }}
               transition={{ type: 'spring', bounce: 0, duration: 0.1 }}
            />
          </div>
        </div>

        {/* Action area */}
        <div className="flex items-center gap-5 w-full md:w-48 justify-center md:justify-end">
          <div className="hidden md:flex items-center gap-5 flex-1 justify-end">
            <Volume2 className="w-5 h-5 text-gray-400 group hover:text-white transition-colors" />
            <div className="w-24 h-1.5 bg-white/5 rounded-full overflow-hidden">
              <div className="w-2/3 h-full bg-gradient-to-r from-blue-500 to-blue-400 rounded-full shadow-lg shadow-blue-500/30" />
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );
};
